    // }


    // console.log(props)
        return (
            <div>
            <div className='main-container' 
            // style={{backgroundImage:`url("https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/6f9f49d2-999e-4a87-897f-3ad19ba212a2/d7rw1io-52da48d0-40f4-442f-8cc6-1ea3604b83ea.gif?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..aPi49PDFERADVZ5Xs4E2KcQc8TnrhtmFKzr3ruyitzk")`}}
            >
                <h1>type in an artist and songa</h1>
                <div className='search-box'>
